      //为true时，才允许被删除
      configurable:true,
      //为true时，该属性才能够出现在对象的枚举属性中
      //出现在对象的枚举属性中，才可以被Object.assign复制
      //了现在对象的枚举属性中，才可能出现在for in循环中
      enumerable:true,
      //为true时，才允许被修改
      //当存在get,set时，不能同时存在value,两者不能同时存在
     // writable:true,
      get:function(){
        console.log('get val');
        return bVal
      },set:function(newVal){
        console.log('set val');
        var container=document.querySelector('.automatic');
        container.innerHTML=newVal;
        bVal=newVal
      }
    });
